describe HotelReviewsController do

  let(:hotel) { FactoryGirl.create(:hotel) }
  let(:member) { FactoryGirl.create(:member) }
  
  # This should return the minimal set of attributes required to create a valid
  # HotelReview. As you add validations to HotelReview, be sure to
  # update the return value of this method accordingly.
  let(:valid_attributes) do
    {
      hotel_id: hotel.id,
      member_id: member.id,
      score: 7
    }
  end

  # This should return the minimal set of values that should be in the session
  # in order to pass any filters (e.g. authentication) defined in
  # HotelReviewsController. Be sure to keep this updated too.
  def valid_session
    {}
  end

  describe "GET index" do
    it "assigns all hotel_reviews as @hotel_reviews" do
      hotel_review = FactoryGirl.create(:hotel_review)
      get :index, {}, valid_session
      assigns(:hotel_reviews).should eq([hotel_review])
    end
  end

  describe "GET show" do
    it "assigns the requested hotel_review as @hotel_review" do
      hotel_review = FactoryGirl.create(:hotel_review)
      get :show, {:id => hotel_review.to_param}, valid_session
      assigns(:hotel_review).should eq(hotel_review)
    end
  end

  describe "GET new" do
    it "assigns a new hotel_review as @hotel_review" do
      get :new, {}, valid_session
      assigns(:hotel_review).should be_a_new(HotelReview)
    end
  end

  describe "GET edit" do
    it "assigns the requested hotel_review as @hotel_review" do
      hotel_review = FactoryGirl.create(:hotel_review)
      get :edit, {:id => hotel_review.to_param}, valid_session
      assigns(:hotel_review).should eq(hotel_review)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new HotelReview" do
        expect {
          post :create, {:hotel_review => valid_attributes}, valid_session
        }.to change(HotelReview, :count).by(1)
      end

      it "assigns a newly created hotel_review as @hotel_review" do
        post :create, {:hotel_review => valid_attributes}, valid_session
        assigns(:hotel_review).should be_a(HotelReview)
        assigns(:hotel_review).should be_persisted
      end

      it "redirects to the created hotel_review" do
        post :create, {:hotel_review => valid_attributes}, valid_session
        response.should redirect_to(HotelReview.last)
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved hotel_review as @hotel_review" do
