        HotelReview.any_instance.stub(:save).and_return(false)
        post :create, {:hotel_review => { "hotel_id" => "invalid value" }}, valid_session
        assigns(:hotel_review).should be_a_new(HotelReview)
      end

      it "re-renders the 'new' template" do
