describe HotelStaysController do

  let(:hotel) { FactoryGirl.create(:hotel) }
  let(:member) { FactoryGirl.create(:member) }

  # This should return the minimal set of attributes required to create a valid
  # HotelStay. As you add validations to HotelStay, be sure to
  # update the return value of this method accordingly.
  let(:valid_attributes) do
    {
      hotel_id: hotel.id,
      member_id: member.id,
      checkout_at: Date.civil(2013, 2, 6)
    }
  end

  # This should return the minimal set of values that should be in the session
  # in order to pass any filters (e.g. authentication) defined in
  # HotelStaysController. Be sure to keep this updated too.
  def valid_session
    {}
  end

  describe "GET index" do
    it "assigns all hotel_stays as @hotel_stays" do
      hotel_stay = FactoryGirl.create(:hotel_stay)
      get :index, {}, valid_session
      assigns(:hotel_stays).should eq([hotel_stay])
    end
  end

  describe "GET show" do
    it "assigns the requested hotel_stay as @hotel_stay" do
      hotel_stay = FactoryGirl.create(:hotel_stay)
      get :show, {:id => hotel_stay.to_param}, valid_session
      assigns(:hotel_stay).should eq(hotel_stay)
    end
  end

  describe "GET new" do
    it "assigns a new hotel_stay as @hotel_stay" do
      get :new, {}, valid_session
      assigns(:hotel_stay).should be_a_new(HotelStay)
    end
  end

  describe "GET edit" do
    it "assigns the requested hotel_stay as @hotel_stay" do
      hotel_stay = FactoryGirl.create(:hotel_stay)
      get :edit, {:id => hotel_stay.to_param}, valid_session
      assigns(:hotel_stay).should eq(hotel_stay)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new HotelStay" do
        expect {
          post :create, {:hotel_stay => valid_attributes}, valid_session
        }.to change(HotelStay, :count).by(1)
      end

      it "assigns a newly created hotel_stay as @hotel_stay" do
        post :create, {:hotel_stay => valid_attributes}, valid_session
        assigns(:hotel_stay).should be_a(HotelStay)
        assigns(:hotel_stay).should be_persisted
      end

      it "redirects to the created hotel_stay" do
        post :create, {:hotel_stay => valid_attributes}, valid_session
        response.should redirect_to(HotelStay.last)
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved hotel_stay as @hotel_stay" do
        # Trigger the behavior that occurs when invalid params are submitted
        HotelStay.any_instance.stub(:save).and_return(false)
        post :create, {:hotel_stay => { "hotel_id" => "invalid value" }}, valid_session
        assigns(:hotel_stay).should be_a_new(HotelStay)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        HotelStay.any_instance.stub(:save).and_return(false)
        post :create, {:hotel_stay => { "hotel_id" => "invalid value" }}, valid_session
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested hotel_stay" do
        hotel_stay = FactoryGirl.create(:hotel_stay)
        # Assuming there are no other hotel_stays in the database, this
        # specifies that the HotelStay created on the previous line
