      it "redirects to the hotel_stay" do
        hotel_stay = FactoryGirl.create(:hotel_stay)
        put :update, {:id => hotel_stay.to_param, :hotel_stay => valid_attributes}, valid_session
        response.should redirect_to(hotel_stay)
